import './App.css';
import React from 'react';
import Nav from 'react-bootstrap/Nav';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import CloseButton from 'react-bootstrap/CloseButton';
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
  return (
    <div className="App">
      <div>
        <Nav className="justify-content-end" activeKey="/home">
          <Nav.Item>
            <Nav.Link href="/home">Todo</Nav.Link>
          </Nav.Item>
          <Nav.Item>
            <Nav.Link eventKey="link-1">Contact</Nav.Link>
          </Nav.Item>
        </Nav>
      </div>

      <Routes>
        <Route path="/" element={
          <>
            <p className="Title">Todo</p>
            <div>
              <Nav className="justify-content-center" activeKey="/home">
                <Nav.Item>
                  <Nav.Link href="/home">All</Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link eventKey="link-1">Completed</Nav.Link>
                </Nav.Item>
                <Nav.Item>
                  <Nav.Link eventKey="link-2">Incompleted</Nav.Link>
                </Nav.Item>
              </Nav>
            </div>

            <div style={{ marginTop : '1em' }}>
              <Form.Control
                type="text"
                id="inputTodo"
                aria-describedby="todoInputBlock"
                placeholder="Type..."
                className="InputTodo"
              />
              <Button className="AddBtn" variant="dark">+</Button>
            </div>
            <div className="FixedWidth TopBlank">
              { CheckExample() }
            </div>
          </>
        }/>
        <Route path="/help" element={
          <>
            <h1>설명란입니다.</h1>
          </>
        }/>
      </Routes>
    </div>
  );
}

function CheckExample() {
  return (
    <Form>
      <div key={"default-checkbox"} className="mb-3">
        <div>
          <Form.Check // prettier-ignore
            type="checkbox"
            id={"default-checkbox"}
            label={"default checkbox"}
            className="CheckBox"
          />
          <CloseButton />
        </div>
        <div>
          <Form.Check
            disabled
            type="checkbox"
            label={"disabled checkbox"}
            id={"disabled-default-checkbox"}
            className="CheckBox"
          />
          <CloseButton />
        </div>
      </div>
    </Form>
  );
}


export default App;
